import { v4 as uuidv4 } from 'uuid';

class Executor {
    constructor(agent, task) {
        this.agent = agent;
        this.task = task;
        this.id = uuidv4();
    }

    async execute() {
        console.log(`Executing task: ${this.task.description}`);
        const result = await this.agent.execute(this.task);
        console.log(`Task completed. Result: ${result}`);
        return result;
    }
}

export { Executor };