import chalk from 'chalk';
import { replayTask } from '../task_replay'; // Assume we'll create this function

export async function replayTaskCommand(taskId: string): Promise<void> {
  try {
    await replayTask(taskId);
    console.log(chalk.green(`Successfully replayed task: ${taskId}`));
  } catch (error) {
    console.error(chalk.red(`Failed to replay task: ${(error as Error).message}`));
  }
}