class PipelineBase {
    constructor() {
        if (new.target === PipelineBase) {
            throw new TypeError("Cannot construct PipelineBase instances directly");
        }
    }

    async run(input) {
        throw new Error("Method 'run' must be implemented.");
    }

    async process(input) {
        throw new Error("Method 'process' must be implemented.");
    }
}

export { PipelineBase };