import chalk from 'chalk';

class Formatter {
  static formatDict(d, indent = 0) {
    let result = '';
    for (const [key, value] of Object.entries(d)) {
      result += ' '.repeat(indent) + `${key}: `;
      if (typeof value === 'object' && value !== null) {
        result += '\n' + this.formatDict(value, indent + 2);
      } else {
        result += `${value}\n`;
      }
    }
    return result;
  }

  static formatStep(step, result) {
    return chalk.cyan(`\n> ${step}`) + '\n' + chalk.yellow(result);
  }

  static formatSubStep(step, result) {
    return chalk.magenta(`\n>> ${step}`) + '\n' + chalk.yellow(result);
  }
}

export { Formatter };