import yaml from 'js-yaml'
import fs from 'fs'


class YamlParser {
    static parse(file_path) {
        try {
            const fileContents = fs.readFileSync(file_path, 'utf8')
            return yaml.load(fileContents)
        } catch(e) {
            console.error(`Error parsing YAML file: ${e}`)
            return null
        }
    }
}


export { YamlParser }
