import path from 'path';
import os from 'os';

function getHomeDir() {
    return os.homedir();
}

function getCrewAIDir() {
    return path.join(getHomeDir(), '.crewai');
}

function getCrewAIConfigPath() {
    return path.join(getCrewAIDir(), 'config.yaml');
}

export {
    getHomeDir,
    getCrewAIDir,
    getCrewAIConfigPath
};