import I18N from './i18n';

class Prompts {
    constructor() {
        this.i18n = new I18N();
    }

    async initialize() {
        await this.i18n.loadPrompts();
    }

    slice(slice) {
        return this.i18n.slice(slice);
    }

    errors(error) {
        return this.i18n.errors(error);
    }

    tools(tool) {
        return this.i18n.tools(tool);
    }
}

export { Prompts };