import { KickoffTaskOutputsSQLiteStorage } from "../memory/storage/kickoff_task_outputs_storage"


class TaskOutputStorageHandler {
    constructor(storage=null) {
        this.storage = storage || new KickoffTaskOutputsSQLiteStorage()
    }

    async storeTaskOutput(taskId, output) {
        await this.storage.store(taskId, output)
    }

    async getTaskOutput(taskId) {
        return await this.storage.get(taskId)
    }
}


export { TaskOutputStorageHandler }
